import React from 'react';
import {
  SafeAreaView,
  StatusBar,
  StyleSheet,
  Button,
  TextInput,
  Text,
  Pressable,
  View,
  Image,
  ScrollView,
} from 'react-native';
import { Dimensions } from 'react-native';
import Ripple from 'react-native-material-ripple';

const Recherche = ({ navigation }) => {
  const boucle = (mot) => {
    let content = [];

    for (let i = 1; i < 21; i++) {
      content.push(
        <View style={styles.ligne} />,
        <Ripple>
          <Text style={styles.texte}>
            {mot} {i}
          </Text>
        </Ripple>
      );
    }

    return content;
  };

  return (
    <View style={{ flex: 1, flexDirection: 'column' }}>
      <StatusBar hidden />
      <View style={{ flex: 2 }}>
        <Pressable
          style={{
            position: 'absolute',
            right: 0,
            flex: 1,
            marginRight: 20,
            marginTop: 30,
          }}
          onPress={() => navigation.navigate('Menu')}>
          <Image source={require('../../../assets/Menu.png')} />
        </Pressable>
      </View>

      <View style={{ flex: 3, paddingRight: 15 }}>
        <TextInput
          style={{
            ...styles.input_and_button,
            fontSize: 24,
            fontWeight: 'bold',
            borderWidth: 1,
          }}
          keyboardType="default"
          placeholder="Destination"
        />
      </View>

      <View style={{ flex: 3, flexDirection: 'row' }}>
        <View style={{ flex: 1, paddingRight: 25 }}>
          <Pressable
            style={{ ...styles.input_and_button, backgroundColor: '#E5E5E5' }}>
            <Text
              style={{ textAlign: 'center', fontSize: 24, fontWeight: 'bold' }}>
              Favoris
            </Text>
          </Pressable>
        </View>

        <View style={{ flex: 1, paddingRight: 15 }}>
          <Pressable
            style={{ ...styles.input_and_button, backgroundColor: '#E5E5E5' }}>
            <Text
              style={{ textAlign: 'center', fontSize: 24, fontWeight: 'bold' }}>
              Récent
            </Text>
          </Pressable>
        </View>
      </View>

      <View style={{ flex: 10, paddingLeft: 23, paddingRight: 22 }}>
        <ScrollView showsVerticalScrollIndicator={false} >{boucle('Destination trouvé')}</ScrollView>
      </View>

      <View style={{ flex: 1 }} />

      <View style={{ flex: 2 }}>
        <Ripple
          style={{
            ...styles.input_and_button,
            backgroundColor: '#C4C4C4',
            marginRight: 15,
          }}
          onPress={() => navigation.navigate('Choix')} >
          <Text
            style={{ textAlign: 'center', fontSize: 24, fontWeight: 'bold' }}>
            Valider
          </Text>
        </Ripple>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  input_and_button: {
    borderRadius: 15,
    padding: 10,
    marginTop: 0,
    marginLeft: 15,
  },
  ligne: {
    borderBottomColor: 'black',
    borderBottomWidth: 1,
  },
  texte: {
    fontSize: 24,
    marginTop: 21,
    marginBottom: 21,
  },
});

export default Recherche;
